<?phprequire_once('../session.start.php');require_once('../init.dataweb.php');if (isset($_REQUEST['simpan']) && isset($_FILES[foto])){		$foto	= $_FILES["foto"];		$dir	= '../../images-content/';		$name_image=$_POST['nama'];				$rs=mysql_query("insert into addgbr set name='$name_image'")or die(mysql_error());		if($foto['type']!=''){			$type = $foto['type'];					if ($type == "image/jpeg") {				$type = "jpg";				}elseif ($type == "image/gif") {					$type = "gif";				}elseif ($type == "image/pjpeg") {					$type = "pjpeg";				}elseif ($type == "image/png") {					$type = "png";			}							if (file_exists("".$dir.$name_image.".jpg")) {					unlink("".$dir.$name_image.".jpg");				}				if (file_exists("".$dir.$name_image.".gif")) {					unlink("".$dir.$name_image.".gif");				}				if (file_exists("".$dir.$name_image.".pjpeg")) {					unlink("".$dir.$name_image.".pjpeg");				}				if (file_exists("".$dir.$name_image.".png")) {					unlink("".$dir.$name_image.".png");				}										copy($foto['tmp_name'], $dir.$name_image.".".$type);			}	if ($rs){			?>		<script language="javascript">			alert ('Berhasil upload gambar !\nSilakan pilih gambar dengan menekan tombol Pilih Gambar');			document.location.href="blank_uploader.php";		</script>	<?	}	}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml" ><head runat="server">    <link rel="Stylesheet" href="../css/style_dataweb_new.css" />    <title></title>    <script language="javascript" src="../script/validasi.js"></script>    <script language="javascript">    	function chg(){		var x=document.getElementById("foto").value;			if (x.length>0){				//document.getElementById("keterangan").style.visibility="visible";				//document.getElementById("jumlah").value="1";				var result = '';				var i=0;				var xxx='';				while (i <= x.length - 1){					if (x.charAt(i)=='.'){						xxx=x.charAt(i);					}					if (xxx.length>0){						result = result + x.charAt(i);					}					i++;				}			document.getElementById("ext").value = result;			document.getElementById("tr").style.background = "#99CCCC" ;			document.getElementById("nama").focus();			}		}		function validate(){			var x=document.getElementById("foto").value;			var n=document.getElementById("nama").value;			var y=1;			if (x.length==0){				alert ('File tidak boleh kosong!');				return false;			} else {				while (y<=3){				var ext=document.getElementById("ext").value;				var file=document.getElementById("foto").value;				if (file.length>0){					/*if (ext!='.JPG' && ext!='.jpg' && ext!='.Jpg' && ext!='.JPg' && ext!='.JPEG' && ext!='.jpeg'){						alert ('Format Gambar harus ber-extensi jpg atau JPG !');						document.getElementById("foto").value='';						document.getElementById("tr").style.background = "#FF8080" ;						return false;					}*/ 				}				y++;				}			}			if (n.length==0){				alert ('Nama gambar harus diisi!');				document.getElementById("nama").focus();				return false;			}		return true;		}    </script></head><body>    <form action="uploader.php" id="form1" runat="server" enctype="multipart/form-data" onSubmit="return validate()" method="post">    <div>    <table border="1" style="border-collapse:collapse;" cellpadding="2" cellspacing="0" width="100%">    <tr id="tr" style="background:#99CCCC">        <td width="16%" align="left" >File Gambar :</td>        <td width="84%" ><input size="60" name="foto" id="foto" type="file" onChange="chg()"><input type="hidden" name="ext" id="ext" size="30"></td>    </tr>    <tr>        <td align="left">Nama :</td>        <td align="left"><input type="text" name="nama" id="nama" size="30"></td>    </tr>    <tr>        <td colspan="2">            <div align="center">              <input size="60" class="but" name="simpan" id="simpan" type="submit" value="Upload">                    </div></td>      </tr>    </table>        </div>    </form></body></html>